Ability.Lua:
if not eclipse then eclipse = &quot;solar&quot; end

if UnitBuffID(&quot;player&quot;, 48517)
then eclipse = &quot;solar&quot;
elseif UnitBuffID(&quot;player&quot;, 48518)
then eclipse = &quot;lunar&quot; end

if eclipse == &quot;solar&quot;
and IsSpellInRange(&quot;Wrath&quot;, &quot;target&quot;)  == 1
then return true end

if eclipse == &quot;lunar&quot;
and IsSpellInRange(&quot;Starfire&quot;, &quot;target&quot;)  == 1
then return true end

if UnitBuffID(&quot;player&quot;, 16870)
and checkSpell(48572, &quot;target&quot;)
then return true end

--if UnitBuffID(&quot;player&quot;, 50334) == nil
if checkSpell(48572, &quot;target&quot;)
then return true end
