Ability.Lua:
local _, FireTotem = GetTotemInfo(1)
local _, EarthTotem = GetTotemInfo(2)
local _, WaterTotem = GetTotemInfo(3)
local _, AirTotem = GetTotemInfo(4)

if WaterTotem == &quot;&quot;
and FireTotem == &quot;&quot;
and AirTotem == &quot;&quot;
and EarthTotem == &quot;&quot;
and inCombat(&quot;player&quot;)
then return true end

hpot = { 33447, 43569, 40087, 41166, 40067 }
for i = 1, #hpot do if getHp(&quot;player&quot;) &lt;= 12 and GetItemCount(hpot[i]) &gt;= 1 and not ItemCooldownRemains(hpot[i]) then UseItemByName(hpot[i]) end end
mpot = { 33448, 43570, 40087, 42545, 39671 }
for i = 1, #mpot do if getMana(&quot;player&quot;) &lt;= 20 and IsBoss() and GetItemCount(mpot[i]) &gt;= 1 and not ItemCooldownRemains(mpot[i]) then UseItemByName(mpot[i]) end end
